import React, {useState} from "react";
import ExpenseForm from "./ExpenseForm";
import './NewExpense.css';

function NewExpense(props){

    // State used to show or not show the form
    const [isVisible, setIsVisible] = useState(false)

    // handles form submission and uses the onAddExpense button passed down from App.js
    function submitHandler(enteredExpenseData){
        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString()
        }
        props.onAddExpense(expenseData)
        setIsVisible(prevState =>{
            return !prevState
        })
    }  

    function updateVisibility(){
        setIsVisible((prevState) => {
            return !prevState
        })
    }

    if(!isVisible){
        return(
            <button onClick={updateVisibility}>Add Expense</button>
        )
    }

    return (
        <div className="new-expense">
            <ExpenseForm onSubmit={submitHandler} />
        </div>
    )
}

export default NewExpense;
