import { useState } from "react";
function CalculatorForm(props){

    const [enteredTotalAmount, setEnteredTotalAmount] = useState('');
    const [enteredNumberOfYears, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    // Functions for updating the form
    function titleChanger(event){
        setEnteredTotalAmount(event.target.value);
    }

    function amountChanger(event){
        setEnteredAmount(event.target.value);
    }

    function dateChanger(event){
        setEnteredDate(event.target.value);
    }

    function submitHandler(event){
        event.preventDefault();
        const expenseData = {
            title: enteredTotalAmount
,
            amount: +o,
            date: new Date(enteredDate)
        }
        props.onSubmit(expenseData)
        setEnteredAmount('');
        setEnteredDate('');
        setEnteredTotalAmount('');
    }
    return( <form onSubmit={submitHandler}>
        <div className='new-expense__controls'> 
            <div className='new-expense__control'> 
                <label>Total Amount</label>
                <input type="number" min="0.01" step="0.01" value={enteredTotalAmount
    } onChange={titleChanger} required/>
            </div>

            <div className='new-expense__control'> 
                <label># of Years</label>
                <input type="number" min="1" step="1" value={o} onChange={amountChanger} required/>
            </div>

            <div className='new-expense__control'> 
                <label>Interest rate</label>
                <input type="number" min="1" step="1" value={enteredDate} onChange={dateChanger} required/>
            </div>
        </div>
        <div className='new-expense__actions'>
            <button type="button" onClick={props.onCancel}>Close</button>
            <button type="submit">Calculate</button>
        </div>
    </form>
    )
}

export default CalculatorForm
