import ExpenseList from "./components/Expenses/ExpenseList"
import NewExpense from "./components/newExpense/NewExpense"
import React, {useState} from "react"
import Defintions from "./components/Definitions/Definitions";

const DUMMY_DATA = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

function App() {
  const [expenses, setExpenses_arr] = useState(DUMMY_DATA);

  function addExpense(enteredExpenseData){
    setExpenses_arr(prevExpenses => {
      return [enteredExpenseData, ...prevExpenses]

    });
  }

  return (
    <div>
      <Defintions/>
      <NewExpense onAddExpense={addExpense}/>
      <ExpenseList expenses = {expenses}/>
    </div>
  )
}

export default App;
