import ExpenseForm from "./ExpenseForm"
import React, {useState} from "react"

const ConditionalForm = props => {

    const [isVisible, setIsVisible] = useState(false)

    // To submit the form
    function submitHandler(enteredExpenseData){
        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString()
        }
        // Adds the new expense to the list
        props.onAddExpense(expenseData);
    }

    // To change the visibility of the form
    function changeVisibility(){ 
        setIsVisible((prevState), !prevState)
    }

    // Form not visible
    if(!isVisible){
        return(
            <button onClick={changeVisibility}>Add Expense</button>
        )
    }

    return(
        <ExpenseForm onSubmit={submitHandler}>
            <button onClick={changeVisibility}>Cancel</button>
        </ExpenseForm>
    )
}

export default ConditionalForm
