import ExpenseItem from "./ExpenseItem";
import Card from '../UI/Card';
import './ExpenseList.css';
import ExpensesFilter from "./ExpensesFilter";
import React, {useState} from 'react';


function ExpenseList(props) {
    const [selectedYear, setSelectedYear] = useState('2020');

    function filterData(yearIn){
        setSelectedYear(yearIn);
    } 

    function filterArray(selectedYear, actualYear){
        return selectedYear == actualYear 
    }

    let unFilteredData = props.expenses;
    let filteredData = unFilteredData.filter(filterArray);
    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter selected={selectedYear} onFilter={filterData}/>
                {filteredData.map(expense_data => 
                    <ExpenseItem
                        key={expense_data.id}
                        title={expense_data.title}
                        amount={expense_data.amount}
                        date={expense_data.date}
                    />
                )}
            </Card>
        </div>
      );
}

export default ExpenseList;
