import './ExpenseForm.css';
import React, {useState} from 'react';

function ExpenseForm(props){
    // States used to add things to the form
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    // State used to show or not show the form
    const [isVisible, updateIsVisible] = useState(false)

    // Functions for updating the form
    function titleChanger(event){
        setEnteredTitle(event.target.value);
    }

    function amountChanger(event){
        setEnteredAmount(event.target.value);
    }

    function dateChanger(event){
        setEnteredDate(event.target.value);
    }

    function submitHandler(event){
        event.preventDefault();
        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        }
        props.onSubmit (expenseData);
        setEnteredAmount('');
        setEnteredDate('');
        setEnteredTitle('');
    }

    // Updates the visibility of the form
    const changeVisibility = () =>{
        updateIsVisible((prevState) => {
            return !prevState
        })
    } 

    // To show only the add expense button
    if(!isVisible){
        return(
            <button onClick={changeVisibility}>Add Expense</button>
        )
    }

    return <form onSubmit={submitHandler}>
        <div className='new-expense__controls'> 
            <div className='new-expense__control'> 
                <label>Title</label>
                <input type="text" value={enteredTitle} onChange={titleChanger}/>
            </div>

            <div className='new-expense__control'> 
                <label>Amount</label>
                <input type="number" min="0.01" step="0.01" value={enteredAmount} onChange={amountChanger}/>
            </div>

            <div className='new-expense__control'> 
                <label>Date</label>
                <input type="date" min="2019-01-01" max="2022-12-31" value={enteredDate} onChange={dateChanger}/>
            </div>
        </div>
        <div className='new-expense__actions'>
            <button onClick={changeVisibility}>Cancel</button>
            <button type="submit">Add Expense</button>
        </div>
    </form>
}

export default ExpenseForm;
