import './ExpenseForm.css';
import React, {useState} from 'react';

function ExpenseForm(props){
    // States used to add things to the form
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    // Functions for updating the form
    function titleChanger(event){
        setEnteredTitle(event.target.value);
    }

    function amountChanger(event){
        setEnteredAmount(event.target.value);
    }

    function dateChanger(event){
        setEnteredDate(event.target.value);
    }

    function submitHandler(event){
        event.preventDefault();
        const expenseData = {
            title: enteredTitle,
            amount: +enteredAmount,
            date: new Date(enteredDate)
        }
        props.onSubmit(expenseData)
        setEnteredAmount('');
        setEnteredDate('');
        setEnteredTitle('');
    }

    return <form onSubmit={submitHandler}>
        <div className='new-expense__controls'> 
            <div className='new-expense__control'> 
                <label>Title</label>
                <input type="text" value={enteredTitle} onChange={titleChanger} required/>
            </div>

            <div className='new-expense__control'> 
                <label>Amount</label>
                <input type="number" min="0.01" step="0.01" value={enteredAmount} onChange={amountChanger} required/>
            </div>

            <div className='new-expense__control'> 
                <label>Date</label>
                <input type="date" min="2019-01-01" max="2023-01-01" value={enteredDate} onChange={dateChanger} required/>
            </div>
        </div>
        <div className='new-expense__actions'>
            <button type="button" onClick={props.onCancel}>Close</button>
            <button type="submit">Add Expense</button>
        </div>
    </form>
}

export default ExpenseForm;
