import './LoanInfo.css'
function LoanInfo(props){
    // compound interest formula
    const amount = props.amount
    const numYears = props.numYears
    const interestRate = props.interestRate
    const temp = amount * ((1 + interestRate) ** numYears)
    const loanTotal = temp.toFixed(2)

    return(
        <center className="loan">
            <div>Loan Info</div>
            <div>Amount: {props.amount}</div>
            <div>Number of Years: {props.numYears}</div>
            <div>Interest Rate: {props.interestRate}</div>
            <div>Loan Total: {loanTotal}</div>
            <button onClick={props.onClose}>Close</button>
        </center>
    )
}

export default LoanInfo
